CREATE TABLE CUSTOMER (ID INTEGER NOT NULL, ADDR_CITY VARCHAR(255), ADDR_COUNTRY VARCHAR(255), ADDR_STATE VARCHAR(255), ADDR_STREET1 VARCHAR(1024), ADDR_STREET2 VARCHAR(1024), ADDR_ZIPCODE VARCHAR(255), EMAIL VARCHAR(1024) NOT NULL, FIRSTNAME VARCHAR(512) NOT NULL, ISACTIVE SMALLINT DEFAULT 0 NOT NULL, LASTNAME VARCHAR(512) NOT NULL, PASSWORD VARCHAR(255) NOT NULL, PHONE VARCHAR(255), USERNAME VARCHAR(255) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE PRODUCT (ID INTEGER NOT NULL, CODE VARCHAR(255) NOT NULL, DESCRIPTION VARCHAR(5000) NOT NULL, DETAILS VARCHAR(10000) NOT NULL, ISACTIVE SMALLINT DEFAULT 0 NOT NULL, NAME VARCHAR(1024) NOT NULL, PRICE FLOAT, QUANTITY INTEGER, PRIMARY KEY (ID))
CREATE TABLE C_Order (ID INTEGER NOT NULL, DELEIVERYSTATUS VARCHAR(255), DELIVERYDATE TIMESTAMP, ORDERDATE TIMESTAMP, PRICE FLOAT, ORDEREDBY_ID INTEGER, PRIMARY KEY (ID))
CREATE TABLE ORDERLINE (ID INTEGER NOT NULL, PRICE FLOAT, QUANTITY INTEGER, ITEM_ID INTEGER, PRIMARY KEY (ID))
CREATE TABLE SHIPMENTLOG (ID INTEGER NOT NULL, LOGTIME TIMESTAMP, STATUS VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE EMAIL (ID INTEGER NOT NULL, MESSAGE VARCHAR(255), MESSAGETIME TIMESTAMP, PRIMARY KEY (ID))
CREATE TABLE NOTIFICATION (ID INTEGER NOT NULL, MESSAGE VARCHAR(255), MESSAGETIME TIMESTAMP, PRIMARY KEY (ID))
CREATE TABLE TRUCK (ID INTEGER NOT NULL, NUMBER INTEGER, PRIMARY KEY (ID))
CREATE TABLE CUSTOMER_EMAIL (Customer_ID INTEGER NOT NULL, Emails_ID INTEGER NOT NULL, PRIMARY KEY (Customer_ID, Emails_ID))
CREATE TABLE C_Order_ORDERLINE (Order_ID INTEGER NOT NULL, Items_ID INTEGER NOT NULL, PRIMARY KEY (Order_ID, Items_ID))
CREATE TABLE C_Order_NOTIFICATION (Order_ID INTEGER NOT NULL, Notifications_ID INTEGER NOT NULL, PRIMARY KEY (Order_ID, Notifications_ID))
CREATE TABLE C_Order_SHIPMENTLOG (Order_ID INTEGER NOT NULL, ShipmentLog_ID INTEGER NOT NULL, PRIMARY KEY (Order_ID, ShipmentLog_ID))
CREATE TABLE TRUCK_C_Order (Truck_ID INTEGER NOT NULL, Orders_ID INTEGER NOT NULL, PRIMARY KEY (Truck_ID, Orders_ID))
ALTER TABLE C_Order ADD CONSTRAINT COrderORDEREDBY_ID FOREIGN KEY (ORDEREDBY_ID) REFERENCES CUSTOMER (ID)
