ALTER TABLE CUSTOMER_EMAIL ADD CONSTRAINT CSTOMEREMAILmilsID FOREIGN KEY (Emails_ID) REFERENCES EMAIL (ID)
ALTER TABLE CUSTOMER_EMAIL ADD CONSTRAINT CSTMREMAILCstmerID FOREIGN KEY (Customer_ID) REFERENCES CUSTOMER (ID)
