ALTER TABLE C_Order_NOTIFICATION ADD CONSTRAINT CrdrNTFICATIONrdrD FOREIGN KEY (Order_ID) REFERENCES C_Order (ID)
ALTER TABLE C_Order_NOTIFICATION ADD CONSTRAINT CrdrNTFCTNNtfctnsD FOREIGN KEY (Notifications_ID) REFERENCES NOTIFICATION (ID)
